// Parse Airtop response and extract email OTP code
const response = $json.response || $json.output || '';

let result = {
  otp_code: '',
  found: false,
  error: '',
  email_subject: '',
  email_body_excerpt: ''
};

try {
  // Remove markdown code blocks if present
  let jsonText = response.replace(/```json\s*/g, '').replace(/```\s*/g, '');
  result = JSON.parse(jsonText);
} catch (e) {
  // Try to find JSON object in text
  const match = response.match(/\{[\s\S]*\}/);
  if (match) {
    try {
      result = JSON.parse(match[0]);
    } catch (e2) {
      // Try to extract 6-digit code directly from text
      const codeMatch = response.match(/\b\d{6}\b/);
      if (codeMatch) {
        result = {
          otp_code: codeMatch[0],
          found: true,
          email_subject: 'Extracted from raw text',
          email_body_excerpt: response.substring(0, 200)
        };
      } else {
        result.error = 'Failed to parse OTP from email response';
      }
    }
  }
}

return {
  json: {
    email_address: $('Workflow Trigger').item.json.email_address,
    otp_code: result.otp_code,
    found: result.found,
    email_subject: result.email_subject || '',
    email_body_excerpt: result.email_body_excerpt || '',
    error: result.error || '',
    scraped_at: new Date().toISOString()
  }
};
